function p(t){return t=t.trim(),t.length>0?"<p>"+t.replace(/[\r\n]+/,"</p><p>")+"</p>":null}function writeStories(t,e,r){e.append('<div class="news-story" id="story-'+t+'"><div class="story-block"><h2></h2><p class="meta"></p><div class="contents"></div></div></div>');var n=r.find("SourceDescription").text(),a=r.find("WebPageURL").text(),s=r.find("TitleText").text(),i=r.find("PublishedDateTimestamp").text(),o=' ... <a href="'+a+'">[More]</a>',c=r.find("ContentText").text();c=c.substr(0,160),i=new Date(i);var l=["January","February","March","April","May","June","July","August","September","October","November","December"];i=l[i.getMonth()]+" "+i.getDay();var p='<a href="'+a+'">'+s+"</a>";$("#story-"+t+" h2").html(p),$("#story-"+t+" .meta").html(n+" | "+i),$("#story-"+t+" .contents").html(c),$("#story-"+t+" .contents").append(o)}