function parseNews(t){execActivityStories=$(t).find("NewsDetails").has('CategoryText:contains("Executive Activity")'),execActivityStories.each(function(t){0==$(this).find("doneflag").length&&(theTitle=$(this).find("TitleText").text(),buildStoryHTML(t,theTitle,"TestDate","Blah blah this is the summary"),$(this).append("<doneflag>Set</doneflag><br>"))})}function buildStoryHTML(t,e,i,s){$("#amazon-stories").append('<div id="news-story'+t+'" class="news-story"><div class="story-block"><h2></h2><p></p></div></div>'),$("#news-story"+t+" h2").html(e),$("#news-story"+t+" p").html(s)}